    "http://localhost:8000/api/auth/login" \
    --header "Content-Type: application/json" \
    --header "Accept: application/json" \
    --data "{
    \"email\": \"curluser@example.com\",
    \"password\": \"Pass123!@#\",
    \"remember_me\": true
}"
</code></pre></div>


